import { Box, Button, TextField, Typography, styled } from '@mui/material';

const Component = styled(Box)`
    width : 400px;
    margin: auto;
    box-shadow : 5px 6px 5px 12px rgb(10 41 116 /0.6);
    margin-top: 7%;
    `
const Image = styled('img')(
    {
        width: 400,
        margin: 'auto',
        display: 'flex',
        padding: '50px , 0 , 0',
    }
)

const Wrapper = styled(Box)`
    padding: 25px 35px;
    display : flex;
    flex : 1;
    flex-direction : column;
    & > div , & > button , p
    {
        margin-top: 20px;
    } 
`;

const LoginButton = styled(Button)`
    text-transform:none;
    background-image: linear-gradient(to right top, #d16ba5, #9c5b93, #6c4b79, #42395a, #232538, #232c3e, #233344, #243a48, #306579, #3694a7, #42c7d0, #5ffbf1);
    height: 48px;
    border-radious: 2px;
    box-shadow : 0px 3px 4px 0px rgb(0 , 0,  0, 350);
    &:hover
    {
        background-color: #a1cdfc;
        box-shadow : 0px 5px 8px 0px rgba(0, 0, 0, 34);
    }
`

const SigninButton = styled(Button)`
    text-transform:none;
    background-image: linear-gradient(to right top, #160810, #1e121c, #261929, #2b2137, #2c2a46, #373856, #424766, #4e5677, #6a6e8d, #8787a4, #a3a1bb, #c0bcd3);
    color: white;
    height: 48px;
    border-radious: 2px;
    box-shadow : 0px 3px 4px 0px rgb(0 0 0/ 80%);
`


const StyledTextField = styled(TextField)`
input
    {
        font-size: 16px; /* Change font size */
        font-family: Arial, sans-serif; /* Change font family */
        font-weight: bold; /* Change font weight */
    }
`;

const Login = () => {
    const imgurl = './Picture/login-picture.jpg';
    return (
        <Component>
            <Box>
                <Image src={imgurl} alt='Login-Image' />
            </Box>
            <Wrapper>
                <StyledTextField id="standard-basic" label="Email" variant="standard" />
                <StyledTextField id="standard-basic" label="Password" type="password" variant="standard" />
                <LoginButton variant="contained">Login</LoginButton>
                <Typography style={{textAlign:'center'}}>Or</Typography>
                <SigninButton>Create an account</SigninButton>
            </Wrapper>
        </Component>
    );
}

export default Login;
