import { Button, styled, Table, TableBody, TableCell, TableHead, TableRow } from "@mui/material";
import { Link } from "react-router-dom";
import { cetegories } from "../../constant/data";

const StyledTable = styled(Table)`
border :1px solid rgba(224, 224 , 224 ,1);
`
const StyledButton = styled(Button)`
  margin: 5%;
  width: 85%;
  background: #6495ed;
  background-color: rgb(12 , 89 ,122 , 44);
  color: #fff;

//   &:hover {
//     background: #6495ed;
//     color: #fff;
//   }
`;
const Categories = () => {
    return (
      <>
        <Link style={{ textDecoration: 'none' }} to={`/createpost? category =${category || '' }`}>
          <StyledButton variant="contained">Create Blog</StyledButton>
        </Link>
        <StyledTable>
          <TableHead>
            <TableRow>
              <Link to='/' />
              <TableCell>All Categories</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {cetegories.map((category, index) => (
              <TableRow key={index}>
                <TableCell>{category.type}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </StyledTable>
      </>
    );
};

export default Categories;
