import { Button, styled, Table, TableBody, TableCell, TableHead, TableRow } from "@mui/material";
import { Link, useSearchParams } from "react-router-dom";
import { cetegories } from "../../constant/data";

const StyledTable = styled(Table)`
border :1px solid rgba(224, 224 , 224 ,1);
`
const StyledButton = styled(Button)`
  margin: 5%;
  width: 85%;
  background: #6495ed;
  background-color: rgb(12 , 89 ,122 , 44);
  color: #fff;

  // &:hover {
  //   background: #6495ed;
  //   color: #fff;
  // }
`;

const StyledLink = styled(Link)`
text-decoration : none;
color : inherit;
`

const Categories = () => {
  const [searchparams] = useSearchParams();
  const category = searchparams.get('category'); 
    return (
      <>
        <Link
          style={{ textDecoration: "none" }}
          to={`/createpost?category=${category || ''}`}
        >
          <StyledButton variant="contained">Create Blog</StyledButton>
        </Link>
        <StyledTable>
          <TableHead>
            <TableRow>
              <TableCell>
                <Link to="/">
                  All Categories
                </Link>
            </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {cetegories.map((category, index) => (
              <TableRow key={index}>
                <TableCell>
                  <Link to={`/?category=${category.type}`}>
                    {category.type}
                  </Link>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </StyledTable>
      </>
    );
};

export default Categories;
