import { Box, Button, TextField, styled, Typography } from '@mui/material';

const Component = styled(Box)`
    width : 400px;
    margin: auto;
    box-shadow : 5px 6px 5px 12px rgb(10 41 116 /0.6);
    margin-top: 7%;
    `
const Image = styled('img')(
    {
        width: 400,
        margin: 'auto',
        display: 'flex',
        padding: '50px , 0 , 0',
    }
)

const Wrapper = styled(Box)`
    padding: 25px 35px;
    display : flex;
    flex : 1;
    flex-direction : column;
    & > div , & > button , p
    {
        margin-top: 20px;
    } 
`;

const LoginButton = styled(Button)`
    text-transform:none;

`

const StyledTextField = styled(TextField)`
input
    {
        font-size: 16px; /* Change font size */
        font-family: Arial, sans-serif; /* Change font family */
        font-weight: bold; /* Change font weight */
    }
`;

const Login = () => {
    const imgurl = './Picture/login-picture.jpg';
    return (
        <Component>
            <Box>
                <Image src={imgurl} alt='Login-Image' />
            </Box>
            <Wrapper>
                <StyledTextField id="standard-basic" label="Email" variant="standard" />
                <StyledTextField id="standard-basic" label="Password" type="password" variant="standard" />
                <Button variant="contained">Login</Button>
                <Typography style={{textAlign:'center'}}>Or</Typography>
                <Button>Create an account</Button>
            </Wrapper>
        </Component>
    );
}

export default Login;
