import { AddCircle as Add } from "@mui/icons-material";
import {
  Box,
  Button,
  FormControl,
  InputBase,
  styled,
  TextareaAutosize,
} from "@mui/material";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { DataContext } from "../../Context/Dataprovider";
import { API } from "../../Service/Api";
import debounce from "lodash/debounce"; // Import debounce function

const Container = styled(Box)`
  margin: 50px 100px;
`;

const Image = styled("img")({
  height: "50vh",
  width: "100%",
  objectFit: "cover",
});

const StyledFormControl = styled(FormControl)`
  margin-top: 10px;
  display: flex;
  flex-direction: row;
`;

const StyledInputBase = styled(InputBase)`
  flex: 1;
  margin: 0 30px;
  font-size: 25px;
`;

const StyledTextareaAutosize = styled(TextareaAutosize)`
  width: 100%;
  margin-top: 10px;
  font-size: 18px;
  border: none;
  &:focus-visible {
    outline: none;
  }
`;

const initialdata = {
  title: "",
  discription: "",
  picture: "",
  username: "",
  categories: "",
  createdDate: new Date(),
};

const CreatePost = () => {
  const [post, setPost] = useState(initialdata);
  const [file, setFile] = useState(null);
  const location = useLocation();
  const { account } = useContext(DataContext);

  // Image url
  const url = post.picture
    ? post.picture
    : "/project_picture/altumcode-dMUt0X3f59Q-unsplash.jpg";

  // Resize Observer Handler
  const handleResize = debounce(() => {
    // Logic to handle resize if needed
  }, 200);

  useEffect(() => {
    const getImage = async () => {
      if (file) {
        const data = new FormData();
        data.append("name", file.name);
        data.append("file", file);

        // Api Call
        try {
          const response = await API.Fileupload(data);
          setPost((prevPost) => ({ ...prevPost, picture: response.data }));
        } catch (error) {
          console.error("Error while uploading picture:", error);
        }
      }
    };

    getImage();

    setPost((prevPost) => ({
      ...prevPost,
      categories: location.search?.split("=")[1] || "All",
      username: account?.username || "",
    }));

    // Attach resize event listener
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [file, location.search, account]);

  const handleInputChange = (e) => {
    if (e && e.target) {
      const { name, value } = e.target;
      setPost((prevPost) => ({ ...prevPost, [name]: value }));
    }
  };

  return (
    <Container>
      {/* Image */}
      <Image src={url} alt="Banner" />

      <StyledFormControl>
        {/* Picture Input */}
        <label htmlFor="FileInput">
          <Add />
        </label>
        <input
          type="file"
          id="FileInput"
          style={{ display: "none" }}
          onChange={(e) => setFile(e.target.files[0])}
        />

        {/* Title input field */}
        <StyledInputBase
          name="title"
          placeholder="Title"
          value={post.title || ""} // Ensure value is always a string
          onChange={handleInputChange}
        />

        {/* Publish Button */}
        <Button variant="contained">Published</Button>
      </StyledFormControl>

      {/* Text Area */}
      <StyledTextareaAutosize
        minRows={7}
        placeholder="About Blog"
        name="discription"
        value={post.discription || ""} // Ensure value is always a string
        onChange={handleInputChange}
      />
    </Container>
  );
};

export default CreatePost;
