import { Box, Button, FormControl, InputBase, styled, TextareaAutosize } from "@mui/material";
import {AddCircle as Add} from "@mui/icons-material";

const Container = styled(Box)`
  margin: 50px 100px;
`;
const Image = styled('img')({
  height: '50vh',
  width: '100%',
  objectFit: 'cover',

});

const StyledFormControl = styled(FormControl)`
margin-top : 10px;
display: flex;
flex-direction:row;
`;

const StyledInputBase = styled(InputBase)`
flex: 1;
margin : 0 30px;
font-size : 25px;
`


const CreatePost = () => {

  const url = "/project_picture/altumcode-dMUt0X3f59Q-unsplash.jpg";
    return (
      <Container>
        <Image
          src={url}
          alt="Banner"
        />
        <StyledFormControl>
          <label htmlFor="FileInput">
            <Add />
          </label>
          <input type="file" name="" id="FileInput" style={{ display: 'none' }} />
          <StyledInputBase placeholder="Title" />
          <Button variant="contained">Published</Button>
        </StyledFormControl>
        <TextareaAutosize minRows={7} placeholder="About Blog" />
      </Container>
    );
};

export default CreatePost;
